import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; set, del &#x7D; from "edit-package-json";

// edit JSON as string
assert.equal(
  set(
    `&#x7B;
  "name": "test",
  "dependencies": &#x7B;
    "ranges-apply": "^2.0.0",
    "string-left-right": "^2.3.30"
  &#x7D;,
  "devDependencies": &#x7B;&#x7D;
&#x7D;`,
    "dependencies.ranges-apply", // path to amend
    "^3.2.2" // new value
  ),
  `&#x7B;
  "name": "test",
  "dependencies": &#x7B;
    "ranges-apply": "^3.2.2",
    "string-left-right": "^2.3.30"
  &#x7D;,
  "devDependencies": &#x7B;&#x7D;
&#x7D;`
);

// edit from JSON string
assert.equal(
  del(
    `&#x7B;
  "name": "test",
  "dependencies": &#x7B;
    "ranges-apply": "^2.0.0",
    "string-left-right": "^2.3.30"
  &#x7D;,
  "devDependencies": &#x7B;&#x7D;
&#x7D;`,
    "devDependencies" // path to delete
  ),
  `&#x7B;
  "name": "test",
  "dependencies": &#x7B;
    "ranges-apply": "^2.0.0",
    "string-left-right": "^2.3.30"
  &#x7D;
&#x7D;`
);

mini-del.js.content:
import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; del &#x7D; from "edit-package-json";

// edit JSON as string
assert.equal(
  del(
    `&#x7B;
  "a": "b",
  "c": "d"
&#x7D;`,
    "c" // path to delete
  ),
  `&#x7B;
  "a": "b"
&#x7D;`
);

mini-set.js.content:
import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; set &#x7D; from "edit-package-json";

// edit JSON as string
assert.equal(
  set(
    `&#x7B;
  "a": "b",
  "c": &#x7B;
    "d": "e"
  &#x7D;
&#x7D;`,
    "c.d", // path
    "x" // value to put
  ),
  `&#x7B;
  "a": "b",
  "c": &#x7B;
    "d": "x"
  &#x7D;
&#x7D;`
);
